import logo from "./logo.svg";
import "./App.css";
import { db } from "./axios";
import { useState } from "react";

function App() {
  const [name, setName] = useState();
  const add = async () => {
    try {
      await db.post(`/add`);
    } catch (e) {
      console.log("THERE IS A PROBLEM", e);
    }
  };
  const get = async () => {
    try {
      const result = await db.get(`/`);
      console.log("This is result", result.data[result.data.length - 1].name);
      setName(result.data[result.data.length - 1].name);
    } catch (e) {
      console.log("THERE IS A PROBLEM", e);
    }
  };
  return (
    <div
      style={{
        borderWidth: 20,
        borderColor: "black",

        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <div
        style={{
          width: "500px",
          height: "500px",
          backgroundColor: "gray",
        }}
      >
        <form
          style={{
            display: "flex",
            backgroundColor: "red",
            width: "70%",
            justifyContent: "space-between",
          }}
        >
          <label>Enter your name:</label>
          <input type="text" />
        </form>
        <button onclick="activateLasers()">Add to DB</button>
        <button onClick={get}>Get from DB</button>
      </div>
    </div>
  );
}

export default App;
